$(document).ready(function(){"use strict";let e=document.querySelectorAll(".password-showHide");e.forEach(e=>{e.addEventListener("click",()=>{e.classList.toggle("hide"),"password"===e.closest(".form-input").querySelector("input").type?e.closest(".form-input").querySelector("input").type="text":e.closest(".form-input").querySelector("input").type="password"})})});